int a[MX], pre[MX];

//size_t tmp;

bool check(int len, int n, int k) {
	vector<int> v(a, a + len);
	for (int it = 0; it < k; it++) {
		int sum = accumulate(v.begin(), v.end(), 0);
		if (sum > n) return false;
		
		reverse(v.begin(), v.end());
		vector<int> nxt;
		for (size_t i = 0; i < v.size(); i++)
			for (int j = 0; j < v[i]; j++)
				nxt.push_back(i + 1);
		
		v = nxt;
	}
	
//	tmp = v.size();
	
	return true;
}

int brute(int p, int n, int k) {
	if (check(p, n, k) == false) return 0;
	
//	pre[tmp]++;
	
	int ans = (p > 0 ? 1 : 0);
	
	for (int x = (p > 0 ? a[p - 1] : 1); x <= n; x++) {
		a[p] = x;
		int f = brute(p + 1, n, k);
		if (f == 0) break;
		ans += f;
	}
	
	return ans;
}

int solveOne(int n) {
	static int dp[MX][MX];
	
	for (int sum = 1; sum <= n; sum++)
		for (int x = sum; x > 0; x--) {
			dp[sum][x] = (x == sum ? 1 : 0);
			if (x + 1 <= sum) {
				dp[sum][x] += dp[sum][x + 1];
				dp[sum][x] %= md;
			}
			
			if (x <= sum - x) {
				dp[sum][x] += dp[sum - x][x];
				dp[sum][x] %= md;
			}
		}
	
	int ans = 0;
	for (int k = 1; k <= n; k++) {
		ans += dp[k][1];
		ans %= md;
	}
	
	return ans;
}

int solveTwo(int n) {
	static vector<int> dp[MX][MX];
	for (int sum = 1; sum <= n; sum++)
		for (int k = 1; k * (k + 1) / 2 <= sum; k++) {
			int mx = sum / (k * (k + 1) / 2);
			dp[sum][k].resize(mx + 1);
			for (int x = mx; x > 0; x--) {
				dp[sum][k][x] = 0;
				if (k == 1 && x == sum) dp[sum][k][x] += 1;
				
				if (x + 1 <= mx) {
					dp[sum][k][x] += dp[sum][k][x + 1];
					dp[sum][k][x] %= md;
				}
				
				if (x < dp[sum - k * x][k - 1].size()) {
					dp[sum][k][x] += dp[sum - k * x][k - 1][x];
					dp[sum][k][x] %= md;
				}
			}
		}
	
	int ans = 0;
	for (int sum = 1; sum <= n; sum++)
		for (int k = 1; k <= n; k++) {
			if (dp[sum][k].size() < 2) continue;
			
			ans += dp[sum][k][1];
			ans %= md;
		}
	
	return ans;
}

