	int n, k;
	ignore = scanf("%d %d", &n, &k);
	
	if (k == 1) printf("%d\n", solveOne(n));
	else if (k == 2) printf("%d\n", solveTwo(n));
	else if (k == 3) printf("%d\n", precalc[n] - 1);
	else printf("%d\n", brute(0, n, k));
	
	return 0;
