#include <bits/stdc++.h>
using namespace std;

using db = double;
using ll = long long;
using ld = long double;
using vint = vector <int>;
using pll = pair <ll, ll>;
using pii = pair <int, int>;
using ull = unsigned long long;

/*
#define db double
#define ll long long
#define ld long double
#define vint vector <int>
#define pll pair <ll, ll>
#define pii pair <int, int>
#define ull unsigned long long
*/

#define fi first
#define se second
#define pb push_back
#define mem(x, v, s) memset(x, v, sizeof(x[0]) * (s))
#define cpy(x, y, s) memcpy(x, y, sizeof(x[0]) * (s))

inline int read() {
	int x = 0; char s = getchar();
	while(!isdigit(s)) s = getchar();
	while(isdigit(s)) x = x * 10 + s - '0', s = getchar();
	return x;
}

const int N = 2e5 + 5;

int n, a[N];
bool Getxor(int n, int *a) {
	bool res = 0;
	for(int i = 1; i <= n; i++) res ^= a[i];
	return res;
}
vector <int> solve(int n) {
	assert(n & 1);
	vector <int> res;
	for(int i = n - 2; i >= 1; i -= 2) res.pb(i);
	for(int i = 1; i <= n - 2; i += 2) res.pb(i);
	return res;
}
int main() {
	int T; cin >> T;
	while(T--) {
		cin >> n;
		for(int i = 1; i <= n; i++) cin >> a[i];
		if(Getxor(n, a)) {
			puts("NO");
			continue;
		}
		if(n & 1) {
			vint res = solve(n);
			puts("YES"), cout << res.size() << endl;
			for(int it : res) cout << it << " "; cout << endl;
		} else {
			bool pre = 0, ok = 0;
			for(int i = 1; i <= n; i++) {
				pre ^= a[i];
				if((i & 1) && !pre) {
					vint left = solve(i);
					vint right = solve(n - i);
					puts("YES"), cout << left.size() + right.size() << endl;
					for(int it : left) cout << it << " ";
					for(int it : right) cout << it + i << " "; cout << endl;
					ok = 1; break;
				}
			}
			if(!ok) puts("NO"); 
		}
	}
	return 0;
}