#include <bits/stdc++.h>
using namespace std;

using db = double;
using ll = long long;
using ld = long double;
using vint = vector <int>;
using pll = pair <ll, ll>;
using pii = pair <int, int>;
using ull = unsigned long long;

/*
#define db double
#define ll long long
#define ld long double
#define vint vector <int>
#define pll pair <ll, ll>
#define pii pair <int, int>
#define ull unsigned long long
*/

#define fi first
#define se second
#define pb push_back
#define mem(x, v, s) memset(x, v, sizeof(x[0]) * (s))
#define cpy(x, y, s) memcpy(x, y, sizeof(x[0]) * (s))

inline int read() {
	int x = 0; char s = getchar();
	while(!isdigit(s)) s = getchar();
	while(isdigit(s)) x = x * 10 + s - '0', s = getchar();
	return x;
}

const ll mod = 1e9 + 7;
const ll iv2 = mod + 1 >> 1;
const int N = 200 + 5;

ll ksm(ll a, ll b = mod - 2) {
	ll s = 1;
	while(b) {
		if(b & 1) s = s * a % mod;
		a = a * a % mod, b >>= 1;
	} return s;
}

struct Number {
	ll a, b;
	Number(ll x = 0, ll y = 0) {a = x, b = y;}
	bool isZero() {return !a && !b;}
	Number operator * (ll x) {return Number(a * x % mod, b * x % mod);}
	Number operator + (Number x)
		{return Number((a + x.a) % mod, (b + x.b) % mod);}
	Number operator - (Number x)
		{return Number((a - x.a + mod) % mod, (b - x.b + mod) % mod);}
	Number operator * (Number x) {
		ll aa = a * x.a + 5 * b * x.b, bb = a * x.b + b * x.a; 
		return Number(aa % mod, bb % mod);
	}
	Number operator / (Number x) {
		ll aa = (a * x.a - 5 * b * x.b % mod + mod) % mod;
		ll bb = (mod - a * x.b % mod + b * x.a) % mod;
		ll c = ksm((x.a * x.a + mod - 5 * x.b * x.b % mod) % mod);
		return Number(aa * c % mod, bb * c % mod);
	}
} A(0, ksm(5)), B(0, mod - ksm(5)), x(iv2, iv2), y(iv2, mod - iv2), I(1, 0);
Number ksm(Number a, ll b) {
	Number s = 1;
	while(b) {
		if(b & 1) s = s * a;
		a = a * a, b >>= 1;
	} return s;
}

ll l, r, k, fc = 1, ans, C[N][N], S[N][N];
Number PowSum(Number x, ll pw) {
	Number iv = I - x;
	if(iv.isZero()) return Number((pw + 1) % mod);
	return (I - ksm(x, pw + 1)) / iv;
}
int main() {
//	cerr << (A * ksm(x, 10) + B * ksm(y, 10)).a << endl;
	cin >> k >> l >> r, l++, r += 2;
	for(int i = 0; i <= k; i++)
		for(int j = 0; j <= i; j++) {
			C[i][j] = (j == 0 || j == i ? 1 : 
				(C[i - 1][j - 1] + C[i - 1][j])) % mod;
			S[i][j] = (j == i ? 1 : (j == 0 ? 0 : 
				(S[i - 1][j - 1] + (i - 1) * S[i - 1][j]))) % mod;
		}
	for(int i = 2; i <= k; i++) fc = fc * i % mod;
	for(int j = 0; j <= k; j++) {
		ll coef = (k - j) & 1 ? mod - S[k][j] : S[k][j];
//		cout << "coef " << coef << endl;
		Number tmp;
		for(int c = 0; c <= j; c++) {
//			cerr << "check " << j << " " << c << endl;
			Number coef2 = ksm(A, c) * ksm(B, j - c) * C[j][c];
			Number base = ksm(x, c) * ksm(y, j - c);
//			Number de = base * coef; cerr << de.a << endl;
//			Number res; for(int d = l + 1; d <= r; d++) res = res + ksm(base, d);
			Number res = PowSum(base, r) - PowSum(base, l);
			tmp = tmp + res * coef2;
		}
//		cerr << "tmp " << tmp.a << " " << tmp.b << endl;
		ans = (ans + coef * tmp.a) % mod;
	} cout << ans * ksm(fc) % mod << endl;
	return 0;
}