#include <bits/stdc++.h>
using namespace std;

using db = double;
using ll = long long;
using ld = long double;
using vint = vector <int>;
using pll = pair <ll, ll>;
using pii = pair <int, int>;
using ull = unsigned long long;

/*
#define db double
#define ll long long
#define ld long double
#define vint vector <int>
#define pll pair <ll, ll>
#define pii pair <int, int>
#define ull unsigned long long
*/

#define fi first
#define se second
#define pb push_back
#define mem(x, v, s) memset(x, v, sizeof(x[0]) * (s))
#define cpy(x, y, s) memcpy(x, y, sizeof(x[0]) * (s))

inline int read() {
	int x = 0; char s = getchar();
	while(!isdigit(s)) s = getchar();
	while(isdigit(s)) x = x * 10 + s - '0', s = getchar();
	return x;
}

const int N = 1e6 + 5;
const ll b1 = 131, b2 = 37;
const ll m1 = 1e9 + 7, m2 = 998244353;

char s[N];
int n, k, ans[N];
ll pw1[N], pw2[N], hs1[N], hs2[N];
void init(ll *hs, ll *pw, ll bs, ll mod) {
	pw[0] = 1;
	for(int i = 1; i <= n; i++) pw[i] = pw[i - 1] * bs % mod;
	for(int i = 1; i <= n; i++) hs[i] = (hs[i - 1] * bs + s[i] - '0') % mod;
}
ll get1(int l, int r) {return l--, (hs1[r] - hs1[l] * pw1[r - l] % m1 + m1) % m1;}
ll get2(int l, int r) {return l--, (hs2[r] - hs2[l] * pw2[r - l] % m2 + m2) % m2;}
pii get(int l, int r) {return make_pair(get1(l, r), get2(l, r));}
int main() {
	cin >> n >> s + 1, k = n + 1 >> 1, ans[k + 1] = -1;
	init(hs1, pw1, b1, m1), init(hs2, pw2, b2, m2);
	for(int i = k; i; i--) {
		int l = i, r = n - i + 1; ans[i] = ans[i + 1] + 2; 
		while(ans[i] > -1) {
			pii tmp = get(l, l + ans[i] - 1);
			if(l + ans[i] <= r && get(l, l + ans[i] - 1) == get(r - ans[i] + 1, r)) break;
			else ans[i] -= 2;
		}
	} for(int i = 1; i <= k; i++) cout << ans[i] << " ";
	cout << endl;
	return 0;
}