__attribute((naked)) void In(int* x){asm("sub $32, %%rsp \n call scanf \n add $32, %%rsp \n ret" : : "c"("%d"), "d"(x));} __attribute((naked)) void Out(int x){asm("sub $32, %%rsp \n call printf \n add $32, %%rsp \n ret" : : "c"("%d\n"), "d"(x));} const int N = 400010; bool a[N]; int b[N]; int q, x; int main(){for(int i = 0; i < N; ++i) b[i]=i; In(&q); In(&x); int ans = 0; while(q--){int z; In(&z); if(b[z%x] < N){b[z%x] += x; z = b[z%x]-x; a[z] = 1;} while(a[ans]) ans++; Out(ans);}}