    ll ans_day = -1;
    int ans = -1;
    for (int i=1;i<=7;i++){
        ll ans_temp = calc(i,a,b,c);
        if (ans_temp > ans_day){
            ans_day = ans_temp;
            ans = i;
        }
    }
