#include <bits/stdc++.h>
using namespace std; int x , y , n , a , b , e[4]; int main() { cin >> n >> a >> b; for(int i = 0 ; i < n ; i++) { cin >> x >> y; x -= a, y -= b; if(x > 0 && y > 0) e[0]++, e[3]++; else if(x > 0 && y < 0) e[2]++, e[3]++; else if(x < 0 && y > 0) e[0]++, e[1]++; else if(x < 0 && y < 0) e[1]++, e[2]++; else if(x == 0 && y > 0) e[0]++; else if(x == 0 && y < 0) e[2]++; else if(y == 0 && x > 0) e[3]++; else e[1]++;} int mx = max({e[0], e[1], e[2], e[3]}); if(e[0] == mx) b++; else if(e[1] == mx) a--; else if(e[2] == mx) b--; else a++; cout << mx << endl << a << " " << b << endl; }