int n,m;
int a[505];
int dp[505][505];
const int Mod=998244353;

int dfs(int l,int r){
	int& ans=dp[l][r];
	if(ans!=-1){
		return ans;
	}
	if(l>=r){
		return ans=1;
	}
	int minx=1e9,whr;
	for(int i=l;i<=r;i++){
		if(a[i]<minx){
			minx=a[i];
			whr=i;
		}
	}
	int lsum=0,rsum=0;
	for(int i=l;i<=whr;i++){
		lsum=(lsum+1LL*dfs(l,i-1)*dfs(i,whr-1))%Mod;
	}
	for(int i=whr+1;i<=r+1;i++){
		rsum=(rsum+1LL*dfs(whr+1,i-1)*dfs(i,r))%Mod;
	}
	return ans=1LL*lsum*rsum%Mod;
}

int main(){
	scanf("%d%d",&n,&m);
	for(int i=1;i<=n;i++){
		scanf("%d",a+i);
	}
	memset(dp,-1,sizeof(dp));
	printf("%d\n",dfs(1,n));
	return 0;
}