int n,m;
char s[505][505];

int main(){
	scanf("%d%d",&n,&m);
	int cnt=0;
	for(int i=1;i<=n;i++){
		scanf("%s",s[i]+1);
		for(int j=1;j<=m;j++){
			cnt+=(s[i][j]=='*');
		}
	}
	for(int i=1;i<=n;i++){
		for(int j=1;j<=m;j++){
			if(s[i][j]!='*'){
				continue;
			}
			int x=0;
			if(s[i-1][j]!='*'||s[i+1][j]!='*'||s[i][j-1]!='*'||s[i][j+1]!='*'){
				continue;
			}
			for(int k=j-1;s[i][k]=='*';k--){
				x++;
			}
			for(int k=j+1;s[i][k]=='*';k++){
				x++;
			}
			for(int k=i-1;s[k][j]=='*';k--){
				x++;
			}
			for(int k=i+1;s[k][j]=='*';k++){
				x++;
			}
			if(x==cnt-1){
				puts("YES");
				return 0;
			}
		}
	}
	puts("NO");
	return 0;
}