string s[100005];
int n;
vector<pair<pair<int,int>,int> > ff;
vector<pair<int,int> > f1,f2;
vector<pair<int,int> > out1,out2;

bool isyy(char c){
	return c=='a'||c=='e'||c=='i'||c=='o'||c=='u';
}

int main(){
	cin>>n;
	for(int i=0;i<n;i++){
		cin>>s[i];
		int cnt=0;
		char lstyy='a';
		for(int j=0;j<s[i].size();j++){
			if(isyy(s[i][j])){
				cnt++;
				lstyy=s[i][j];
			}
		}
		ff.push_back(make_pair(make_pair(cnt,lstyy),i));
	}
	sort(ff.begin(),ff.end());
	int lst=-1,lstyy=' ',cnt;
	int lst0t=-1,lstpos;
	for(int i=0;i<n;i++){
		if(ff[i].first.first!=lst){
			lst=ff[i].first.first;
			if(lst0t!=-1){
				lst0t=-1;
			}
		}
		if(ff[i].first.second!=lstyy){
			lstyy=ff[i].first.second;
		}
		if(i<n-1&&ff[i+1].first.first==ff[i].first.first&&ff[i+1].first.second==ff[i].first.second){
			f2.push_back(make_pair(ff[i].second,ff[i+1].second));
			i++;
		}else{
			if(lst0t==-1){
				lst0t=ff[i].first.first;
				lstpos=ff[i].second;
			}else{
				lst0t=-1;
				f1.push_back(make_pair(lstpos,ff[i].second));
			}
		}
	}
	int ans=min((f1.size()+f2.size())/2,f2.size());
	cout<<ans<<endl;
	for(int i=0;i<ans;i++){
		out2.push_back(f2[i]);
	}
	for(int i=ans;i<f2.size()&&ans;i++){
		out1.push_back(f2[i]);
		ans--;
	}
	for(int i=0;ans;i++){
		out1.push_back(f1[i]);
		ans--;
	}
	for(int i=0;i<out1.size();i++){
		cout<<s[out1[i].first]<<' '<<s[out2[i].first]<<endl;
		cout<<s[out1[i].second]<<' '<<s[out2[i].second]<<endl;
	}
	return 0;
}