bool isint(double x){
	return fabs(x-floor(x))<=1e-8||fabs(x-ceil(x))<=1e-8;
}

double a[100005];
int n;
int canjar[100005];
long long sum=0,cnt=0;

int main(){
	scanf("%d",&n);
	for(int i=0;i<n;i++){
		scanf("%lf",a+i);
		canjar[i]=(isint(a[i])?0:1);
		sum+=floor(a[i]);
	}
	int tojar=-sum;
	for(int i=0;i<n;i++){
		printf("%.0lf\n",floor(a[i])+(canjar[i]==1&&tojar>0?1:0));
		tojar-=(canjar[i]==1&&tojar>0?1:0);
	}
	return 0;
}