int u[1000005],v[1000005];
vector<pair<int,int> > egs;
int n,m;
int deg[1000005],deg2[1000005];
bool fuck[1000005];

int main(){
	scanf("%d%d",&n,&m);
	for(int i=0;i<m;i++){
		scanf("%d%d",u+i,v+i);
		egs.emplace_back(u[i],v[i]);
		deg[u[i]]++;
		deg[v[i]]++;
		deg2[u[i]]++;
		deg2[v[i]]++;
	}
	random_shuffle(egs.begin(),egs.end());
	for(int i=0;i<m;i++){
		tie(u[i],v[i])=egs[i];
	}
	int x=m;
	for(int i=0;i<m;i++){
		if(deg2[u[i]]>(deg[u[i]]+1)/2&&deg2[v[i]]>(deg[v[i]]+1)/2){
			fuck[i]=1;
			deg2[u[i]]--;
			deg2[v[i]]--;
			x--;
		}
	}
	printf("%d\n",x);
	for(int i=0;i<m;i++){
		if(!fuck[i]){
			printf("%d %d\n",u[i],v[i]);
		}
	}
	return 0;
}