int n,m;
vector<char> grf[50005];
int cnth[50005],cntl[50005];

void solve(){
	scanf("%d%d",&n,&m);
	for(int i=0;i<n;i++){
		cnth[i]=0;
	}
	for(int i=0;i<m;i++){
		cntl[i]=0;
	}
	for(int i=0;i<n;i++){
		for(int j=0;j<m;j++){
			char ch;
			scanf("%c",&ch);
			if(ch=='\n'){
				scanf("%c",&ch);
			}
			grf[i].push_back(ch);
		}
	}
	for(int i=0;i<n;i++){
		for(int j=0;j<m;j++){
			if(grf[i][j]=='*'){
				cnth[i]++;
				cntl[j]++;
			}
		}
	}
	int ans=2333333;
	for(int i=0;i<n;i++){
		for(int j=0;j<m;j++){
			ans=min(ans,m-cnth[i]+n-cntl[j]-(grf[i][j]=='.'));
		}
	}
	printf("%d\n",ans);
	for(int i=0;i<n;i++){
		grf[i].clear();
	}
}

int main(){
	int q;
	scanf("%d",&q);
	while(q--){
		solve();
	}
	return 0;
}