int n;
int a[200005];

int main(){
	scanf("%d",&n);
	int pos;
	for(int i=1;i<=n;i++){
		scanf("%d",a+i);
		if(a[i]==n){
			pos=i;
		}
	}
	int l=pos-1,r=pos+1;
	for(int i=n-1;i;i--){
		if(a[l]==i){
			l--;
		}else if(a[r]==i){
			r++;
		}else{
			puts("NO");
			return 0;
		}
	}
	puts("YES");
	return 0;
}