int n,k;
int a[300005],b[300005];

int main(){
	scanf("%d%d",&n,&k);
	for(int i=1;i<=n;i++){
		scanf("%d",a+i);
	}
	long long ans=a[n]-a[1];
	for(int i=1;i<n;i++){
		b[i]=a[i]-a[i+1];
	}
	sort(b+1,b+n);
	for(int i=1;i<k;i++){
		ans+=b[i];
	}
	printf("%lld\n",ans);
	return 0;
}