int n,m,k;
long long b[300005];
long long ans=0;

int main(){
	scanf("%d%d%d",&n,&m,&k);
	memset(b,0x3f,sizeof(b));
	b[0]=0;
	long long qzh=0;
	for(int i=1;i<=n;i++){
		int x;
		scanf("%d",&x);
		qzh+=x;
		b[i%m]=min(b[i%m],qzh-1ll*(i/m)*k);
		for(int j=0;j<m;j++){
			long long fx=qzh-1ll*(i/m)*k;
			if(i%m>j){
				fx-=k;
			}
			ans=max(ans,fx-b[j]);
		}
	}
	printf("%lld\n",ans);
	return 0;
}