void add(long &a,long b){
	a+=b;
	if(a>=Mod){
		a-=Mod;
	}
}

struct BitchIsThere{
	int N;
	pill dt[500005];
	void upd(pill &a,pill b){
		if(a.yi>b.yi){
			a=b;
		}else if(a.yi==b.yi){
			add(a.er,b.er);
		}
	}
	void init(int nn){
		N=nn;
		for(int i=1;i<=nn;i++){
			dt[i]=mei(Mod,0);
		}
	}
	void insert(int x,pill num){
		while(x<=N){
			upd(dt[x],num);
			x+=x&-x;
		}
	}
	pill qsum(int x){
		pill ans=mei(Mod,0);
		while(x){
			upd(ans,dt[x]);
			x-=x&-x;
		}
		return ans;
	}
}bit;

int n;
vector<pill> pills;

int main(){
	scanf("%d",&n);
	vector<int> ds;
	ds.push_back(-1);
	ds.push_back(0);
	for(int i=0;i<n;i++){
		int x,y;
		scanf("%d%d",&x,&y);
		pills.push_back(mei(y,x));
		ds.push_back(x);
		ds.push_back(y);
	}
	sort(ds.begin(),ds.end());
	ds.resize(unique(ds.begin(),ds.end())-ds.begin());
	sort(pills.begin(),pills.end());
	bit.init(ds.size());
	bit.insert(1,mei(0,1));
	pill ans=mei(Mod,0);
	for(pill p:pills){
		int x=lower_bound(ds.begin(),ds.end(),p.yi)-ds.begin();
		int y=lower_bound(ds.begin(),ds.end(),p.er)-ds.begin();
		pill res=bit.qsum(x);
		if(p.er>pills.back().yi){
			bit.upd(ans,mei(res.yi+p.yi,res.er));
		}else{
			bit.insert(y,mei(p.yi+res.yi-p.er,res.er));
		}
	}
	printf("%lld\n",ans.er);
	return 0;
}