int b[55][55];
int a[55][55];
int n,m;

int main(){
	scanf("%d%d",&n,&m);
	for(int i=1;i<=n;i++){
		for(int j=1;j<=m;j++){
			scanf("%d",b[i]+j);
		}
	}
	vector<pair<int,int> > q;
	for(int i=1;i<n;i++){
		for(int j=1;j<m;j++){
			if(b[i][j]==1&&b[i][j+1]==1&&b[i+1][j]==1&&b[i+1][j+1]==1){
				q.emplace_back(i,j);
				a[i][j]=a[i][j+1]=a[i+1][j]=a[i+1][j+1]=1;
			}
		}
	}
	for(int i=1;i<=n;i++){
		for(int j=1;j<=m;j++){
			if(a[i][j]!=b[i][j]){
				puts("-1");
				return 0;
			}
		}
	}
	printf("%d\n",(int)q.size());
	for(int i=0;i<q.size();i++){
		printf("%d %d\n",q[i].first,q[i].second);
	}
	return 0;
}