int T;
int n,a,b;
long long dp[200005][2];
int x[200005];
const long long Inf=0x3f3f3f3f3f3f3f3fLL;

void solve(){
	scanf("%d%d%d",&n,&a,&b);
	for(int i=1;i<=n;i++){
		scanf("%1d",x+i);
	}
	dp[0][0]=b;
	dp[0][1]=Inf;
	for(int i=1;i<=n;i++){
		dp[i][0]=dp[i][1]=Inf;
		for(int j=0;j<2;j++){
			if(x[i]>j||x[i+1]>j){
				continue;
			}
			for(int k=0;k<2;k++){
				dp[i][j]=min(dp[i][j],dp[i-1][k]+((j!=k)+1)*a+(j+1)*b);
			}
		}
	}
	printf("%lld\n",dp[n][0]);
}

int main(){
	scanf("%d",&T);
	while(T--){
		solve();
	}
	return 0;
}