long dp[55][2][2];
int n;
long m;
int a[55];

long Getdp(int l,int r,int ls,int rls){
	if(l>r){
		return 1;
	}
	long &ans=dp[l][ls][rls];
	if(~ans){
		return ans;
	}
	ans=0;
	for(int i=0;i<2;i++){
		for(int j=0;j<2;j++){
			if((a[l]==i||a[l]==-1)&&(a[r]==j||a[r]==-1)){
				if(l==r&&i!=j){
					continue;
				}
				if(!ls&&i>j){
					continue;
				}
				if(!rls&&i>(!j)){
					continue;
				}
				ans+=Getdp(l+1,r-1,ls||i<j,rls||i<(!j));
			}
		}
	}
	return ans;
}

int main(){
	scanf("%d%lld",&n,&m);
	m++;
	memset(a,-1,sizeof(a));
	memset(dp,-1,sizeof(dp));
	a[1]=0;
	if(Getdp(1,n,0,0)<m){
		puts("-1");
		return 0;
	}
	for(int i=2;i<=n;i++){
		memset(dp,-1,sizeof(dp));
		a[i]=0;
		long ans=Getdp(1,n,0,0);
		if(m>ans){
			a[i]=1;
			m-=ans;
		}
	}
	for(int i=1;i<=n;i++){
		printf("%d",a[i]);
	}
	return 0;
}