abstract class InputReader {
	private boolean finished = false;

	public abstract int read();

	public int readInt() {
		int c = read();
		while (isSpaceChar(c))
			c = read();
		int sgn = 1;
		if (c == '-') {
			sgn = -1;
			c = read();
		}
		int res = 0;
		do {
			if (c < '0' || c > '9')
				throw new InputMismatchException();
			res *= 10;
			res += c - '0';
			c = read();
		} while (!isSpaceChar(c));
		return res * sgn;
	}

	public String readString() {
		int c = read();
		while (isSpaceChar(c))
			c = read();
		StringBuffer res = new StringBuffer();
		do {
			res.appendCodePoint(c);
			c = read();
		} while (!isSpaceChar(c));
		return res.toString();
	}

	private boolean isSpaceChar(int c) {
		return c == ' ' || c == '\n' || c == '\r' || c == '\t' || c == -1;
	}

	public void setFinished(boolean finished) {
		this.finished = finished;
	}

	public abstract void close();
}

class StreamInputReader extends InputReader {
	private InputStream stream;
	private byte[] buf = new byte[1024];
	private int curChar, numChars;

	public StreamInputReader(InputStream stream) {
		this.stream = stream;
	}

	public int read() {
		if (numChars == -1)
			throw new InputMismatchException();
		if (curChar >= numChars) {
			curChar = 0;
			try {
				numChars = stream.read(buf);
			} catch (IOException e) {
				throw new InputMismatchException();
			}
			if (numChars <= 0)
				return -1;
		}
		return buf[curChar++];
	}

	public void close() {
		try {
			stream.close();
		} catch (IOException ignored) {
		}
	}
}

class Exit {
	private Exit() {
	}

	public static void exit(InputReader in, PrintWriter out) {
		in.setFinished(true);
		in.close();
		out.close();
	}
}

interface Solver {
	public void solve(int testNumber, InputReader in, PrintWriter out);
}

class IOUtils {

	public static int[] readIntArray(InputReader in, int size) {
		int[] array = new int[size];
		for (int i = 0; i < size; i++)
			array[i] = in.readInt();
		return array;
	}

	}

class TaskB implements Solver {
	public void solve(int testNumber, InputReader in, PrintWriter out) {
		int theoremCount = in.readInt();
		int taskCount = in.readInt();
		int[] knowledge = IOUtils.readIntArray(in, theoremCount);
		boolean[] inTask = new boolean[theoremCount];
		int max = Integer.MIN_VALUE;
		int min = Integer.MAX_VALUE;
		int knownTasks = in.readInt();
		int taskSize = theoremCount / taskCount;
		for (int i = 0; i < knownTasks; i++) {
			int currentScore = 0;
			for (int j = 0; j < taskSize; j++) {
				int index = in.readInt() - 1;
				currentScore += knowledge[index];
				inTask[index] = true;
			}
			min = Math.min(min, currentScore);
			max = Math.max(max, currentScore);
		}
		List<Integer> unknown = new ArrayList<Integer>();
		for (int i = 0; i < theoremCount; i++) {
			if (!inTask[i])
				unknown.add(knowledge[i]);
		}
		Collections.sort(unknown);
		if ((theoremCount - unknown.size()) / taskSize != taskCount) {
			int score = 0;
			for (int i = 0; i < taskSize; i++)
				score += unknown.get(i);
			min = Math.min(min, score);
			score = 0;
			for (int i = unknown.size() - taskSize; i < unknown.size(); i++)
				score += unknown.get(i);
			max = Math.max(max, score);
		}
		out.printf("%.8f %.8f\n", (double)min / taskSize, (double)max / taskSize);
	}
}