abstract class InputReader {
	private boolean finished = false;

	public abstract int read();

	public int readInt() {
		int c = read();
		while (isSpaceChar(c))
			c = read();
		int sgn = 1;
		if (c == '-') {
			sgn = -1;
			c = read();
		}
		int res = 0;
		do {
			if (c < '0' || c > '9')
				throw new InputMismatchException();
			res *= 10;
			res += c - '0';
			c = read();
		} while (!isSpaceChar(c));
		return res * sgn;
	}

	public String readString() {
		int c = read();
		while (isSpaceChar(c))
			c = read();
		StringBuffer res = new StringBuffer();
		do {
			res.appendCodePoint(c);
			c = read();
		} while (!isSpaceChar(c));
		return res.toString();
	}

	private boolean isSpaceChar(int c) {
		return c == ' ' || c == '\n' || c == '\r' || c == '\t' || c == -1;
	}

	public void setFinished(boolean finished) {
		this.finished = finished;
	}

	public abstract void close();
}

class StreamInputReader extends InputReader {
	private InputStream stream;
	private byte[] buf = new byte[1024];
	private int curChar, numChars;

	public StreamInputReader(InputStream stream) {
		this.stream = stream;
	}

	public int read() {
		if (numChars == -1)
			throw new InputMismatchException();
		if (curChar >= numChars) {
			curChar = 0;
			try {
				numChars = stream.read(buf);
			} catch (IOException e) {
				throw new InputMismatchException();
			}
			if (numChars <= 0)
				return -1;
		}
		return buf[curChar++];
	}

	@Override
	public void close() {
		try {
			stream.close();
		} catch (IOException ignored) {
		}
	}
}

class Exit {
	private Exit() {
	}

	public static void exit(InputReader in, PrintWriter out) {
		in.setFinished(true);
		in.close();
		out.close();
	}
}

interface Solver {
	public void solve(int testNumber, InputReader in, PrintWriter out);
}

class IOUtils {
	public static void printArray(int[] array, PrintWriter out) {
		if (array.length == 0) {
			out.println();
			return;
		}
		out.print(array[0]);
		for (int i = 1; i < array.length; i++)
			out.print(" " + array[i]);
		out.println();
	}

	public static int[] readIntArray(InputReader in, int size) {
		int[] array = new int[size];
		for (int i = 0; i < size; i++)
			array[i] = in.readInt();
		return array;
	}

	}

class TaskC implements Solver {
	public void solve(int testNumber, InputReader in, PrintWriter out) {
		int markCount = in.readInt();
		int first = in.readInt();
		int second = in.readInt();
		int[] marks = IOUtils.readIntArray(in, markCount);
		int[] firstMarks = new int[6];
		int[] secondMarks = new int[6];
		int[] result = new int[markCount];
		if (first == second) {
			for (int i = 0; i < first; i++)
				result[i] = 1;
			for (int i = first; i < markCount; i++)
				result[i] = 2;
		} else {
			int[] marksCopy = marks.clone();
			Arrays.sort(marksCopy);
			int[] small = first > second ? firstMarks : secondMarks;
			int[] high = first > second ? secondMarks : firstMarks;
			for (int i = 0; i < Math.max(first, second); i++)
				small[marksCopy[i]]++;
			for (int i = Math.max(first, second); i < markCount; i++)
				high[marksCopy[i]]++;
			for (int i = 0; i < markCount; i++) {
				if (firstMarks[marks[i]] != 0) {
					result[i] = 1;
					firstMarks[marks[i]]--;
				} else
					result[i] = 2;
			}
		}
		IOUtils.printArray(result, out);
	}
}