abstract class InputReader {
	private boolean finished = false;

	public abstract int read();

	public int readInt() {
		int c = read();
		while (isSpaceChar(c))
			c = read();
		int sgn = 1;
		if (c == '-') {
			sgn = -1;
			c = read();
		}
		int res = 0;
		do {
			if (c < '0' || c > '9')
				throw new InputMismatchException();
			res *= 10;
			res += c - '0';
			c = read();
		} while (!isSpaceChar(c));
		return res * sgn;
	}

	public String readString() {
		int c = read();
		while (isSpaceChar(c))
			c = read();
		StringBuffer res = new StringBuffer();
		do {
			res.appendCodePoint(c);
			c = read();
		} while (!isSpaceChar(c));
		return res.toString();
	}

	private boolean isSpaceChar(int c) {
		return c == ' ' || c == '\n' || c == '\r' || c == '\t' || c == -1;
	}

	public void setFinished(boolean finished) {
		this.finished = finished;
	}

	public abstract void close();
}

class StreamInputReader extends InputReader {
	private InputStream stream;
	private byte[] buf = new byte[1024];
	private int curChar, numChars;

	public StreamInputReader(InputStream stream) {
		this.stream = stream;
	}

	public int read() {
		if (numChars == -1)
			throw new InputMismatchException();
		if (curChar >= numChars) {
			curChar = 0;
			try {
				numChars = stream.read(buf);
			} catch (IOException e) {
				throw new InputMismatchException();
			}
			if (numChars <= 0)
				return -1;
		}
		return buf[curChar++];
	}

	public void close() {
		try {
			stream.close();
		} catch (IOException ignored) {
		}
	}
}

class Exit {
	private Exit() {
	}

	public static void exit(InputReader in, PrintWriter out) {
		in.setFinished(true);
		in.close();
		out.close();
	}
}

interface Solver {
	public void solve(int testNumber, InputReader in, PrintWriter out);
}

class TaskA implements Solver {
	public void solve(int testNumber, InputReader in, PrintWriter out) {
		int length = in.readInt();
		char[][] answer = new char[4][length];
		for (int i = 0; i < length; i += 4) {
			if (i + 1 < length) {
				answer[0][i] = answer[0][i + 1] = 'a';
				answer[1][i] = answer[1][i + 1] = 'b';
			}
			if (i + 3 < length) {
				answer[0][i + 2] = answer[0][i + 3] = 'c';
				answer[1][i + 2] = answer[1][i + 3] = 'd';
			}
			if (i + 2 < length) {
				answer[2][i + 2] = answer[2][i + 1] = 'e';
				answer[3][i + 2] = answer[3][i + 1] = 'f';
			}
			if (i + 4 < length) {
				answer[2][i + 3] = answer[2][i + 4] = 'g';
				answer[3][i + 3] = answer[3][i + 4] = 'h';
			}
		}
		if (length % 2 == 1) {
			answer[0][length - 1] = 'i';
			answer[1][length - 1] = 'i';
			answer[2][0] = 'j';
			answer[3][0] = 'j';
		} else {
			answer[2][length - 1] = 'i';
			answer[3][length - 1] = 'i';
			answer[2][0] = 'j';
			answer[3][0] = 'j';
		}
		for (char[] row : answer)
			out.println(row);
	}
}