abstract class InputReader {
	private boolean finished = false;

	public abstract int read();

	public int readInt() {
		int c = read();
		while (isSpaceChar(c))
			c = read();
		int sgn = 1;
		if (c == '-') {
			sgn = -1;
			c = read();
		}
		int res = 0;
		do {
			if (c < '0' || c > '9')
				throw new InputMismatchException();
			res *= 10;
			res += c - '0';
			c = read();
		} while (!isSpaceChar(c));
		return res * sgn;
	}

	public String readString() {
		int c = read();
		while (isSpaceChar(c))
			c = read();
		StringBuffer res = new StringBuffer();
		do {
			res.appendCodePoint(c);
			c = read();
		} while (!isSpaceChar(c));
		return res.toString();
	}

	private boolean isSpaceChar(int c) {
		return c == ' ' || c == '\n' || c == '\r' || c == '\t' || c == -1;
	}

	public void setFinished(boolean finished) {
		this.finished = finished;
	}

	public abstract void close();
}

class StreamInputReader extends InputReader {
	private InputStream stream;
	private byte[] buf = new byte[1024];
	private int curChar, numChars;

	public StreamInputReader(InputStream stream) {
		this.stream = stream;
	}

	public int read() {
		if (numChars == -1)
			throw new InputMismatchException();
		if (curChar >= numChars) {
			curChar = 0;
			try {
				numChars = stream.read(buf);
			} catch (IOException e) {
				throw new InputMismatchException();
			}
			if (numChars <= 0)
				return -1;
		}
		return buf[curChar++];
	}

	public void close() {
		try {
			stream.close();
		} catch (IOException ignored) {
		}
	}
}

class Exit {
	private Exit() {
	}

	public static void exit(InputReader in, PrintWriter out) {
		in.setFinished(true);
		in.close();
		out.close();
	}
}

interface Solver {
	public void solve(int testNumber, InputReader in, PrintWriter out);
}

class TaskB implements Solver {
	public void solve(int testNumber, InputReader in, PrintWriter out) {
		Map<String, Integer> types = new HashMap<String, Integer>();
		types.put("void", 0);
		int queryCount = in.readInt();
		for (int i = 0; i < queryCount; i++) {
			String operator = in.readString();
			if ("typedef".equals(operator)) {
				Integer normalized = nomalize(in.readString(), types);
				types.put(in.readString(), normalized);
			} else {
				Integer normalized = nomalize(in.readString(), types);
				if (normalized == null)
					out.println("errtype");
				else {
					out.print("void");
					for (int j = 0; j < normalized; j++)
						out.print("*");
					out.println();
				}
			}
		}
	}

	private Integer nomalize(String s, Map<String, Integer> types) {
		int index1 = 0;
		for (int i = 0; i < s.length(); i++) {
			if (s.charAt(i) != '&') {
				index1 = i;
				break;
			}
		}
		int index2 = 0;
		for (int i = s.length() - 1; i >= 0; i--) {
			if (s.charAt(i) != '*') {
				index2 = i + 1;
				break;
			}
		}
		Integer result = types.get(s.substring(index1, index2));
		if (result == null)
			return null;
		result += s.length() - index2 - index1;
		if (result < 0)
			return null;
		return result;
	}
}