abstract class InputReader {
	private boolean finished = false;

	public abstract int read();

	public int readInt() {
		int c = read();
		while (isSpaceChar(c))
			c = read();
		int sgn = 1;
		if (c == '-') {
			sgn = -1;
			c = read();
		}
		int res = 0;
		do {
			if (c < '0' || c > '9')
				throw new InputMismatchException();
			res *= 10;
			res += c - '0';
			c = read();
		} while (!isSpaceChar(c));
		return res * sgn;
	}

	public String readString() {
		int c = read();
		while (isSpaceChar(c))
			c = read();
		StringBuffer res = new StringBuffer();
		do {
			res.appendCodePoint(c);
			c = read();
		} while (!isSpaceChar(c));
		return res.toString();
	}

	private boolean isSpaceChar(int c) {
		return c == ' ' || c == '\n' || c == '\r' || c == '\t' || c == -1;
	}

	public char readCharacter() {
		int c = read();
		while (isSpaceChar(c))
			c = read();
		return (char) c;
	}

	public void setFinished(boolean finished) {
		this.finished = finished;
	}

	public abstract void close();
}

class StreamInputReader extends InputReader {
	private InputStream stream;
	private byte[] buf = new byte[1024];
	private int curChar, numChars;

	public StreamInputReader(InputStream stream) {
		this.stream = stream;
	}

	public int read() {
		if (numChars == -1)
			throw new InputMismatchException();
		if (curChar >= numChars) {
			curChar = 0;
			try {
				numChars = stream.read(buf);
			} catch (IOException e) {
				throw new InputMismatchException();
			}
			if (numChars <= 0)
				return -1;
		}
		return buf[curChar++];
	}

	public void close() {
		try {
			stream.close();
		} catch (IOException ignored) {
		}
	}
}

class Exit {
	private Exit() {
	}

	public static void exit(InputReader in, PrintWriter out) {
		in.setFinished(true);
		in.close();
		out.close();
	}
}

interface Solver {
	public void solve(int testNumber, InputReader in, PrintWriter out);
}

class IOUtils {

	public static String[] readStringArray(InputReader in, int size) {
		String[] array = new String[size];
		for (int i = 0; i < size; i++)
			array[i] = in.readString();
		return array;
	}

	}

class TaskA implements Solver {
	public void solve(int testNumber, InputReader in, PrintWriter out) {
		int wordCount = in.readInt();
		String[] badWords = IOUtils.readStringArray(in, wordCount);
		String name = in.readString();
		char lucky = in.readCharacter();
		boolean[] mustReplace = new boolean[name.length()];
		for (int i = 0; i < name.length(); i++) {
			for (String word : badWords) {
				if (i + word.length() <= name.length() && name.substring(i, i + word.length()).equalsIgnoreCase(word))
					Arrays.fill(mustReplace, i, i + word.length(), true);
			}
		}
		char notLucky = Character.toLowerCase(lucky) == 'a' ? 'b' : 'a';
		StringBuilder answer = new StringBuilder(name.length());
		for (int i = 0; i < name.length(); i++) {
			if (mustReplace[i]) {
				if (Character.isUpperCase(name.charAt(i))) {
					if (Character.toUpperCase(lucky) == name.charAt(i))
						answer.append(Character.toUpperCase(notLucky));
					else
						answer.append(Character.toUpperCase(lucky));
				} else {
					if (Character.toLowerCase(lucky) == name.charAt(i))
						answer.append(Character.toLowerCase(notLucky));
					else
						answer.append(Character.toLowerCase(lucky));
				}
			} else
				answer.append(name.charAt(i));
		}
		out.println(answer);
	}
}