import java.util.*;
import java.io.*;
import java.lang.reflect.Array;
import java.math.BigInteger;

public class C {
	FastScanner in;
	PrintWriter out;
	boolean systemIO = true;

	public class Pair {
		int x;
		int y;

		public Pair(int x, int y) {
			this.x = x;
			this.y = y;
		}
	}
	
	

	public void solve() {
		int n = in.nextInt();
		Stack<Long> a = new Stack<>();
		Stack<Long> b = new Stack<>();
		for (int i = 0; i < n; i++) {
			a.add(in.nextLong());
		}
		for (int i = 0; i < n; i++) {
			b.add(in.nextLong());
		}
		Collections.sort(a);
		Collections.sort(b);
		int th = 0;
		long score = 0;
		while (!a.isEmpty() || !b.isEmpty()) {
			if (th == 0) {
				if (b.isEmpty()) {
					score += a.pop();
				} else if (a.isEmpty()) {
					b.pop();
				} else {
					if (a.peek() >= b.peek()) {
						score += a.pop();
					} else {
						b.pop();
					}
				}
			} else {
				if (a.isEmpty()) {
					score -= b.pop();
				} else if (b.isEmpty()) {
					a.pop();
				} else {
					if (b.peek() >= a.peek()) {
						score -= b.pop();
					} else {
						a.pop();
					}
				}
			}
			th = 1 - th;
		}
		out.println(score);
	}

	public void run() {
		try {
			if (systemIO) {
				in = new FastScanner(System.in);
				out = new PrintWriter(System.out);
			} else {
				in = new FastScanner(new File("input.txt"));
				out = new PrintWriter(new File("output.txt"));
			}
			solve();

			out.close();
		} catch (IOException e) {
			e.printStackTrace();
		}
	}

	class FastScanner {
		BufferedReader br;
		StringTokenizer st;

		FastScanner(File f) {
			try {
				br = new BufferedReader(new FileReader(f));
			} catch (FileNotFoundException e) {
				e.printStackTrace();
			}
		}

		FastScanner(InputStream f) {
			br = new BufferedReader(new InputStreamReader(f));
		}

		String nextLine() {
			try {
				return br.readLine();
			} catch (IOException e) {
				return null;
			}
		}

		String next() {
			while (st == null || !st.hasMoreTokens()) {
				try {
					st = new StringTokenizer(br.readLine());
				} catch (IOException e) {
					e.printStackTrace();
				}
			}
			return st.nextToken();
		}

		int nextInt() {
			return Integer.parseInt(next());
		}

		long nextLong() {
			return Long.parseLong(next());
		}

		double nextDouble() {
			return Double.parseDouble(next());
		}

	}

	// 
	public static void main(String[] arg) {
		new C().run();
	}
}