import java.util.*;
import java.io.*;
import java.math.BigInteger;

public class D {
	FastScanner in;
	PrintWriter out;
	boolean systemIO = true;

	
	public class Pair {
		int v;
		int i;

		public Pair(int v, int i) {
			this.v = v;
			this.i = i;
		}
	}
	
	long mod = 998244353;
	
	public void solve() {
		long n = in.nextLong();
		long[] fact = new long[(int) n];
		fact[(int) (n - 1)] = n;
		for (long i = n - 2; i >= 0; i--) {
			fact[(int) i] = fact[(int) (i + 1)] * (i + 1) % mod;
		}
		long ans = 0;
		for (int i = 0; i < fact.length; i++) {
			ans += fact[0] - fact[i];
			ans %= mod;
			ans = (ans + mod) % mod;
		}
		out.println((ans + fact[0]) % mod);
	}

	public void run() {
		try {
			if (systemIO) {
				in = new FastScanner(System.in);
				out = new PrintWriter(System.out);
			} else {
				in = new FastScanner(new File("input.txt"));
				out = new PrintWriter(new File("output.txt"));
			}
			solve();

			out.close();
		} catch (IOException e) {
			e.printStackTrace();
		}
	}

	class FastScanner {
		BufferedReader br;
		StringTokenizer st;

		FastScanner(File f) {
			try {
				br = new BufferedReader(new FileReader(f));
			} catch (FileNotFoundException e) {
				e.printStackTrace();
			}
		}

		FastScanner(InputStream f) {
			br = new BufferedReader(new InputStreamReader(f));
		}

		String nextLine() {
			try {
				return br.readLine();
			} catch (IOException e) {
				return null;
			}
		}

		String next() {
			while (st == null || !st.hasMoreTokens()) {
				try {
					st = new StringTokenizer(br.readLine());
				} catch (IOException e) {
					e.printStackTrace();
				}
			}
			return st.nextToken();
		}

		int nextInt() {
			return Integer.parseInt(next());
		}

		long nextLong() {
			return Long.parseLong(next());
		}

		double nextDouble() {
			return Double.parseDouble(next());
		}

	}

	// 
	public static void main(String[] arg) {
		new D().run();
	}
}