import java.util.*;

import java.io.*;
import java.lang.reflect.Array;
import java.math.BigInteger;

public class C {
	FastScanner in;
	PrintWriter out;
	boolean systemIO = true;

	public static void quickSort(int[] a, int from, int to) {
		if (to - from <= 1) {
			return;
		}
		int i = from;
		int j = to - 1;
		int x = a[from + (new Random()).nextInt(to - from)];
		while (i <= j) {
			while (a[i] < x) {
				i++;
			}
			while (a[j] > x) {
				j--;
			}
			if (i <= j) {
				int t = a[i];
				a[i] = a[j];
				a[j] = t;
				i++;
				j--;
			}
		}
		quickSort(a, from, j + 1);
		quickSort(a, j + 1, to);
	}

	public long gcd(long x, long y) {
		if (y == 0) {
			return x;
		}
		if (x == 0) {
			return y;
		}
		return gcd(y, x % y);
	}

	public boolean prime(long x) {
		for (int i = 2; i * i <= x; i++) {
			if (x % i == 0) {
				return false;
			}
		}
		return true;
	}

	public long pow(long x, long p) {
		if (p == 0) {
			return 1;
		}
		long t = pow(x, p / 2);
		t *= t;
		t %= mod;
		if (p % 2 == 1) {
			t *= x;
			t %= mod;
		}
		return t;
	}

	public class Pair implements Comparable<Pair> {
		long x;
		long y;

		public Pair(long x, long y) {
			this.x = x;
			this.y = y;
		}

		@Override
		public int compareTo(Pair o) {
			if (x > o.x) {
				return 1;
			}
			if (x < o.x) {
				return -1;
			}
			if (y > o.y) {
				return 1;
			}
			if (y < o.y) {
				return -1;
			}
			return 0;
		}
	}

	long mod = 1000000007;
	
	ArrayList<Integer>[] to;
	HashSet<Integer> used = new HashSet<>();
	
	public void solve() {
		int n = in.nextInt();
		int k = in.nextInt();
		if (n % 2 == 1) {
			int[] x = new int[(n + 1) / 2];
			int t = (n - k) / 2 + 1;
			for (int i = 0; i < x.length; i++) {
				if (i % t != 0) {
					x[i] = 1;
				}
			}
			for (int i = 0; i < n / 2; i++) {
				out.print(x[x.length - 1 - i]);
			}
			for (int i = 0; i < x.length; i++) {
				out.print(x[i]);
			}
		} else {
			if (n == k) {
				for (int i = 0; i < n; i++) {
					out.print(0);
				}
			} else if (k == n - 2) {
				for (int i = 0; i < n; i++) {
					out.print(i % 2);
				}
			} else {
				int[] x = new int[(n + 1) / 2];
				int t = (n - k) / 2 + 1;
				for (int i = 0; i < x.length; i++) {
					if (i % t != 0 && i % t != t - 1) {
						x[i] = 1;
					}
				}
				for (int i = 0; i < n / 2; i++) {
					out.print(x[x.length - 1 - i]);
				}
				for (int i = 0; i < x.length; i++) {
					out.print(x[i]);
				}
			}
		}
	}

	private void dfs(int i) {
		used.add(i);
		for (int j : to[i]) {
			if (!used.contains(j)) {
				dfs(j);
			}
		}
	}

	public void run() {
		try {
			if (systemIO) {
				in = new FastScanner(System.in);
				out = new PrintWriter(System.out);
			} else {
				in = new FastScanner(new File("input.txt"));
				out = new PrintWriter(new File("output.txt"));
			}
			solve();

			out.close();
		} catch (IOException e) {
			e.printStackTrace();
		}
	}

	class FastScanner {
		BufferedReader br;
		StringTokenizer st;

		FastScanner(File f) {
			try {
				br = new BufferedReader(new FileReader(f));
			} catch (FileNotFoundException e) {
				e.printStackTrace();
			}
		}

		FastScanner(InputStream f) {
			br = new BufferedReader(new InputStreamReader(f));
		}

		String nextLine() {
			try {
				return br.readLine();
			} catch (IOException e) {
				return null;
			}
		}

		String next() {
			while (st == null || !st.hasMoreTokens()) {
				try {
					st = new StringTokenizer(br.readLine());
				} catch (IOException e) {
					e.printStackTrace();
				}
			}
			return st.nextToken();
		}

		int nextInt() {
			return Integer.parseInt(next());
		}

		long nextLong() {
			return Long.parseLong(next());
		}

		double nextDouble() {
			return Double.parseDouble(next());
		}

	}

	// 
	public static void main(String[] arg) {
		new C().run();
	}
}