import java.util.*;
import java.io.*;

public class C {
	FastScanner in;
	PrintWriter out;
	boolean systemIO = true;


	
	public void solve() {
		int n = in.nextInt();
		if (n % 4 == 0) {
			int c = n / 4;
			out.println(c * 3 + " " + 1L * 5 * c);
			out.close();
			return;
		}
		boolean flag = false;
		if (n % 2 == 0) {
			n /= 2;
			flag = true;
		}
		if (n == 1) {
			out.print(-1);
			out.close();
			return;
		}
		long k = 1L * n * n;
		long x = (k - 1) / 2;
		if (flag) {
			out.print(2 * x + " " + 2 * (x + 1));
		} else {
			out.print(x + " " + (x + 1));
		} 
	}
	
	public void run() {
		try {
			if (systemIO) {
				in = new FastScanner(System.in);
				out = new PrintWriter(System.out);
			} else {
				in = new FastScanner(new File("segments.in"));
				out = new PrintWriter(new File("segments.out"));
			}
			solve();

			out.close();
		} catch (IOException e) {
			e.printStackTrace();
		}
	}

	class FastScanner {
		BufferedReader br;
		StringTokenizer st;

		FastScanner(File f) {
			try {
				br = new BufferedReader(new FileReader(f));
			} catch (FileNotFoundException e) {
				e.printStackTrace();
			}
		}

		FastScanner(InputStream f) {
			br = new BufferedReader(new InputStreamReader(f));
		}

		String nextLine() {
			try {
				return br.readLine();
			} catch (IOException e) {
				return null;
			}
		}

		String next() {
			while (st == null || !st.hasMoreTokens()) {
				try {
					st = new StringTokenizer(br.readLine());
				} catch (IOException e) {
					e.printStackTrace();
				}
			}
			return st.nextToken();
		}

		int nextInt() {
			return Integer.parseInt(next());
		}

		long nextLong() {
			return Long.parseLong(next());
		}

		double nextDouble() {
			return Double.parseDouble(next());
		}

	}

	// 
	public static void main(String[] arg) {
		new C().run();
	}
}