import java.util.*;
import java.io.*;

public class C {
	FastScanner in;
	PrintWriter out;
	boolean systemIO = true;

	public void solve() {
		String s = in.next();
		int i = 0;
		int n = s.length();
		while (i < n && s.charAt(i) == 'a') {
			i++;
		}
		if (i == n) {
			for (int j = 0; j < s.length() - 1; j++) {
				out.print("a");
			}
			out.print("z");
			return;
		}
		int x = i;
		while (i < n && s.charAt(i) != 'a') {
			i++;
		}
		for (int j = 0; j < x; j++) {
			out.print(s.charAt(j));
		}
		for (int j = x; j < i; j++) {
			out.print((char) (s.charAt(j) - 1));
		}
		for (int j = i; j < n; j++) {
			out.print(s.charAt(j));
		}
	}

	public void run() {
		try {
			if (systemIO) {
				in = new FastScanner(System.in);
				out = new PrintWriter(System.out);
			} else {
				in = new FastScanner(new File("segments.in"));
				out = new PrintWriter(new File("segments.out"));
			}
			solve();

			out.close();
		} catch (IOException e) {
			e.printStackTrace();
		}
	}

	class FastScanner {
		BufferedReader br;
		StringTokenizer st;

		FastScanner(File f) {
			try {
				br = new BufferedReader(new FileReader(f));
			} catch (FileNotFoundException e) {
				e.printStackTrace();
			}
		}

		FastScanner(InputStream f) {
			br = new BufferedReader(new InputStreamReader(f));
		}

		String nextLine() {
			try {
				return br.readLine();
			} catch (IOException e) {
				return null;
			}
		}

		String next() {
			while (st == null || !st.hasMoreTokens()) {
				try {
					st = new StringTokenizer(br.readLine());
				} catch (IOException e) {
					e.printStackTrace();
				}
			}
			return st.nextToken();
		}

		int nextInt() {
			return Integer.parseInt(next());
		}

		long nextLong() {
			return Long.parseLong(next());
		}

		double nextDouble() {
			return Double.parseDouble(next());
		}

	}

	// 
	public static void main(String[] arg) {
		new C().run();
	}
}