import java.util.*;

import javax.swing.Painter;

import java.io.*;

public class D {
	FastScanner in;
	PrintWriter out;
	boolean systemIO = true;
	
	
	public static void quickSort(long[] a, int from, int to) {
		if (to - from <= 1) {
			return;
		}
		int i = from;
		int j = to - 1;
		long x = a[from + (new Random()).nextInt(to - from)];
		while (i <= j) {
			while (a[i] < x) {
				i++;
			}
			while (a[j] > x) {
				j--;
			}
			if (i <= j) {
				long t = a[i];
				a[i] = a[j];
				a[j] = t;
				i++;
				j--;
			}
		}
		quickSort(a, from, j + 1);
		quickSort(a, j + 1, to);
	}
	
	
	public void solve() throws IOException {
		int n = in.nextInt();
		int o = 0;
		int[] v = new int[n];
		int size = 0;
		int th = 0;
		int ans = 0;
		for (int i = 0; i < v.length; i++) {
			int t = in.nextInt();
			if (t == 5) {
				size = 0;
			} else if (t == 4) {
				o = 0;
			} else if (t == 6) {
				o++;
			} else if (t == 3) {
				v[size] = in.nextInt();
				if (v[size] >= th) {
					size++;
				} else {
					ans++;
				}
			} else if (t == 2) {
				ans += o;
				o = 0;
			} else if (t == 1) {
				th = in.nextInt();
				while (size > 0 && v[size - 1] < th) {
					size--;
					ans++;
				}
			}
		}
		out.println(ans);
	}

	public void run() throws IOException {
		if (systemIO) {
			in = new FastScanner(System.in);
			out = new PrintWriter(System.out);
		} else {
			in = new FastScanner(new File("input.txt"));
			out = new PrintWriter(new File("output.txt"));
		}
		solve();

		out.close();
	}

	class FastScanner {
		BufferedReader br;
		StringTokenizer st;

		FastScanner(File f) {
			try {
				br = new BufferedReader(new FileReader(f));
			} catch (FileNotFoundException e) {
				e.printStackTrace();
			}
		}

		FastScanner(InputStream f) {
			br = new BufferedReader(new InputStreamReader(f));
		}

		String nextLine() {
			try {
				return br.readLine();
			} catch (IOException e) {
				return null;
			}
		}

		String next() {
			while (st == null || !st.hasMoreTokens()) {
				try {
					st = new StringTokenizer(br.readLine());
				} catch (IOException e) {
					e.printStackTrace();
				}
			}
			return st.nextToken();
		}

		int nextInt() {
			return Integer.parseInt(next());
		}

		long nextLong() {
			return Long.parseLong(next());
		}

		double nextDouble() {
			return Double.parseDouble(next());
		}

	}

	// 
	public static void main(String[] arg) throws IOException {
		new D().run();
	}
}