import java.util.*;
import java.io.*;

public class C {
	FastScanner in;
	PrintWriter out;
	boolean systemIO = true;

	public static long dfs(int v) {
		used[v] = true;
		long min = c[v];
		for (int i : list[v]) {
			if (!used[i]) {
				min = Math.min(min, dfs(i));
			}
		}
		return min;
	}
	
	static long[] c;
	static boolean[] used;
	static ArrayList<Integer>[] list;
	
	public void solve() {
		int n = in.nextInt();
		int m = in.nextInt();
		c = new long[n];
		for (int i = 0; i < c.length; i++) {
			c[i] = in.nextLong();
		}
		used = new boolean[n];
		list = new ArrayList[n];
		for (int i = 0; i < list.length; i++) {
			list[i] = new ArrayList();
		}
		for (int i = 0; i < m; i++) {
			int x = in.nextInt() - 1;
			int y = in.nextInt() - 1;
			list[x].add(y);
			list[y].add(x);
		}
		long ans = 0L;
		for (int i = 0; i < c.length; i++) {
			if (!used[i]) {
				ans += dfs(i);
			}
		}
		out.print(ans);
	}

	public void run() {
		try {
			if (systemIO) {
				in = new FastScanner(System.in);
				out = new PrintWriter(System.out);
			} else {
				in = new FastScanner(new File("segments.in"));
				out = new PrintWriter(new File("segments.out"));
			}
			solve();

			out.close();
		} catch (IOException e) {
			e.printStackTrace();
		}
	}

	class FastScanner {
		BufferedReader br;
		StringTokenizer st;

		FastScanner(File f) {
			try {
				br = new BufferedReader(new FileReader(f));
			} catch (FileNotFoundException e) {
				e.printStackTrace();
			}
		}

		FastScanner(InputStream f) {
			br = new BufferedReader(new InputStreamReader(f));
		}

		String nextLine() {
			try {
				return br.readLine();
			} catch (IOException e) {
				return null;
			}
		}

		String next() {
			while (st == null || !st.hasMoreTokens()) {
				try {
					st = new StringTokenizer(br.readLine());
				} catch (IOException e) {
					e.printStackTrace();
				}
			}
			return st.nextToken();
		}

		int nextInt() {
			return Integer.parseInt(next());
		}

		long nextLong() {
			return Long.parseLong(next());
		}

		double nextDouble() {
			return Double.parseDouble(next());
		}

	}

	// 
	public static void main(String[] arg) {
		new C().run();
	}
}