const int MAXM = 2000;
const int INF = 1e9;
const int MOD = 1e9 + 7;

int dp[MAXB][MAXN][MAXM];
int sum_pref(int x, int b){
	if (x == 0){
		return 0;
	}
	vc<int> number;
	while(x > 0){
		number.pb(x % b);
		x /= b;
	}
	int ans = 0;
	for (int l = 1; l < len(number); l++){
		for (int d = 1; d < b; d++){
			ans += dp[b][l - 1][(1 << d)];
		}
	}
	int mask = 0;
	reverse(all(number));
	for (int i = 0; i < len(number); i++){
		for (int d = (i == 0 ? 1 : 0); d < number[i]; d++){
			ans += dp[b][len(number) - i - 1][mask ^ (1 << d)];
		}
		mask ^= (1 << number[i]);
	}
	ans += dp[b][0][mask];
	return ans;
}

class EMedalonSalazaraSlizerina {
public:
	void solve(std::istream& in, std::ostream& out) {
		for (int b = 2; b < MAXB; b++){
			dp[b][0][0] = 1;
			for (int i = 0; i + 1 < MAXN; i++){
				for (int mask = 0; mask < (1 << b); mask++) {
					for (int digit = 0; digit < b; digit++){
						dp[b][i + 1][mask ^ (1 << digit)] += dp[b][i][mask];
					}
				}
			}
		}
		int q;
		in >> q;
		while(q--){
			int b, l, r;
			in >> b >> l >> r;
			out << sum_pref(r, b) - sum_pref(l - 1, b) << '\n';
		}
	}
};


