ostream& operator<<(ostream &out, const vector<int> &b)
{
	out << "{";
	for (auto t : b) out << t << ' ';
	out << "}";
	return out;
}

const int maxn = 2030;
const int MOD = 998244353;

int n, k;
//int answer3[maxn];

bool expand(vector<int> cur, int curk)
{
	//cout << curk << ' ' << cur.size() << ' ' << cur << endl;
	if ((int)cur.size() > n) return false;
	if (curk == k)
	{
		//answer3[(int)cur.size()]++;
		return true;
	}
	int sum = accumulate(all(cur), 0);
	if (sum > n) return false;
	sort(all(cur));
	reverse(all(cur));
	vector<int> nxt;
	for (int i = 0; i < (int)cur.size(); i++)
	{
		for (int j = 0; j < cur[i]; j++) nxt.pb(i + 1);
	}
	return expand(nxt, curk + 1);
}

const int maxcnt = 70;

vector<int> all;
int answer = 0;
int ans[maxn][maxn];
//int ans2[maxcnt][maxn][maxn];

void go(int cur, int mx)
{
	for (int v = 1; v <= mx; v++)
	{
		all.pb(v);
		//cout << "try " << v << endl;
		if (expand(all, 0))
		{
			answer++;
			go(cur + 1, v);
		} else
		{
			all.pop_back();
			break;
		}
		all.pop_back();
	}
}

inline void upd(int &a, int b)
{
	a = (a + b) % MOD;
}

void solve1()
{
	ans[0][1] = 1;
	for (int s = 0; s <= n; s++)
	{
		for (int l = 1; l <= n; l++)
		{
			upd(ans[s][l + 1], ans[s][l]);
			if (s + l <= n) upd(ans[s + l][l], ans[s][l]);
		}
	}
	int curans = 0;
	for (int i = 1; i <= n; i++) upd(curans, ans[i][n]);
	printf("%d\n", curans);
}

