bool Check(const vector <int> &V, int n, int k)
{
    int sum = 0;
    for (int i = 0; i < V.size(); i++)
        sum += V[i];
    if (sum > n) return false;
    if (k == 1) return true;
    vector <int> nxt;
    for (int i = int(V.size()) - 1, p = 1; i >= 0; i--, p++)
        for (int j = 0; j < V[i]; j++) nxt.push_back(p);
    return Check(nxt, n, k - 1);
}

void Gen(vector <int> &V, int from, int n, int k)
{
    V.push_back(from - 1);
    while (true) {
        V.back()++;
        if (!Check(V, n, k)) break;
        res++;
        Gen(V, V.back(), n, k);
    }
    V.pop_back();
}

int main()
{
    int n, k; scanf("%d %d", &n, &k);
    if (k == 1) {
        dp[0][0] = 1;
        for (int i = 1; i < Maxn; i++)
            for (int j = 0; j < Maxn; j++) {
                dp[i][j] = dp[i - 1][j];
                if (j >= i) dp[i][j] = (dp[i][j] + dp[i][j - i]) % mod;
            }
        int res = 0;
        for (int j = 1; j <= n; j++)
            res = (res + dp[n][j]) % mod;
        printf("%d\n", res);
    } else if (k == 2)
        printf("%d\n", prec[n]);
    else {
        vector <int> tmp;
        Gen(tmp, 1, n, k);
        printf("%d\n", res);
    }
    return 0;
}