#include<bits/stdc++.h>
using namespace std;
long long dp[500][500];
int n,k,x,a[500];

int main(){
    scanf("%d%d%d",&n,&k,&x);
    for(int i=1;i<=n;i++)
        scanf("%d",&a[i]);
    if(k*(x+1)<=n){
        cout<<"-1\n";
        return 0;
        }
    for(int i=0;i<=400;i++)
        for(int j=0;j<=400;j++)
            dp[i][j]=-2;
        dp[0][0]=0;
        for(int i=1;i<=n;i++){
            for(int j=1;j<=x;j++){
                for(int l=1;l<=k;l++){
                    if(i>=l){
                    if(dp[i-l][j-1]!=-2)
                    dp[i][j]=max(dp[i][j],dp[i-l][j-1]+a[i]);
                    }
                    else
                        break;
                }
            }
        }
    long long ans=-1e18;
    for(int i=n-k+1;i<=n;i++){
        ans=max(ans,dp[i][x]);
    }
        printf("%lld",ans);


}
