#include <iostream>
#include <fstream>

#include <iostream>

class BMergeIt {
public:
	void solve(std::istream& in, std::ostream& out) {
	    int t,n;
	    in >> t;
	    while(t--)
        {
	        in >> n;
	        int a[3]={0},x;
	        for(int i=0;i<n;i++) { in >> x; a[x%3]++; }
	        int ans = a[0];

	        int p = std::min(a[1],a[2]);

	        a[1]-=p;
	        a[2]-=p;

	        ans+=a[1]/3 + a[2]/3;
	        out << ans+p << "\n";

        }

	}
};


