#define pb push_back
#define sz(v) ((int)(v).size())
#define all(v) (v).begin(),(v).end()
#define mp make_pair

using namespace std;

typedef long long int64;
typedef vector<int> vi;
typedef pair<int, int> ii;

using mint = atcoder::modint1000000007;

constexpr int MAX_QUERY = (int) 1e6;

class GSongOfTheSirens {
 public:
  void solveOne(istream &in, ostream &out) {
    mt19937 rng(std::chrono::system_clock::now().time_since_epoch().count());
    int m1 = uniform_int_distribution<int>((int) 5e8, (int) 1e9)(rng);
    while (!atcoder::internal::is_prime_constexpr(m1)) ++m1;
    int m2 = uniform_int_distribution<int>((int) 5e8, (int) 1e9)(rng);
    while (!atcoder::internal::is_prime_constexpr(m2)) ++m2;

    using dmint1 = atcoder::dynamic_modint<1>;
    using dmint2 = atcoder::dynamic_modint<2>;
    dmint1::set_mod(m1);
    dmint2::set_mod(m2);
    int base = 3137;

    int n, q;
    in >> n >> q;
    string s0, t;
    in >> s0 >> t;
    vector<string> s;
    s.push_back(s0);
    for (int i = 0; i < n && s.back().size() < MAX_QUERY; ++i) {
      string old = s.back();
      old += t[i];
      old += s.back();
      s.push_back(old);
    }
    string main = s.back();
    vector<dmint1> pw1(max((int) main.size(), MAX_QUERY) + 1);
    vector<dmint2> pw2(max((int) main.size(), MAX_QUERY) + 1);
    pw1[0] = 1;
    pw2[0] = 1;
    for (int i = 1; i < pw1.size(); ++i) {
      pw1[i] = base * pw1[i - 1];
      pw2[i] = base * pw2[i - 1];
    }
    vector<dmint1> prefix1(main.size() + 1);
    vector<dmint2> prefix2(main.size() + 1);
    vector<dmint1> suffix1(main.size() + 1);
    vector<dmint2> suffix2(main.size() + 1);
    for (int i = 1; i <= main.size(); ++i) {
      prefix1[i] = prefix1[i - 1] + main[i - 1] * pw1[i - 1];
      prefix2[i] = prefix2[i - 1] + main[i - 1] * pw2[i - 1];
      suffix1[i] = suffix1[i - 1] * base + main[main.size() - i];
      suffix2[i] = suffix2[i - 1] * base + main[main.size() - i];
    }
    vector<vector<mint>> extraCount(n + 1, vector<mint>(26));
    vector<mint> pow2(n + 1);
    pow2[0] = 1;
    for (int i = 1; i < pow2.size(); ++i) {
      pow2[i] = 2 * pow2[i - 1];
    }
    vector<mint> invpow2(n + 1);
    invpow2[0] = 1;
    mint inv2 = mint(2).inv();
    for (int i = 1; i < invpow2.size(); ++i) {
      invpow2[i] = inv2 * invpow2[i - 1];
    }
    for (int i = s.size(); i <= n; ++i) {
      extraCount[i] = extraCount[i - 1];
      extraCount[i][t[i - 1] - 'a'] += invpow2[i];
    }

    for (int qi = 0; qi < q; ++qi) {
      int k;
      string w;
      in >> k;
      in >> w;
      mint res = 0;
      if (w.size() <= s0.size()) {
        string t = w;
        t += "#";
        t += s0;
        auto v = atcoder::z_algorithm(t);
        mint cur = 0;
        for (int i = w.size() + 1; i < t.size(); ++i) {
          if (v[i] == w.size()) {
            ++cur;
          }
        }
        res += cur * pow2[k];
      }
      vector<dmint1> p1(w.size() + 1);
      vector<dmint1> s1(w.size() + 1);
      vector<dmint2> p2(w.size() + 1);
      vector<dmint2> s2(w.size() + 1);
      for (int i = 1; i <= w.size(); ++i) {
        p1[i] = p1[i - 1] + w[i - 1] * pw1[i - 1];
        p2[i] = p2[i - 1] + w[i - 1] * pw2[i - 1];
        s1[i] = s1[i - 1] * base + w[w.size() - i];
        s2[i] = s2[i - 1] * base + w[w.size() - i];
      }
      for (int pos = 0; pos < w.size(); ++pos) {
        char ch = w[pos];
        bool match = false;
        int rem = w.size() - 1 - pos;
        int worst = max(pos, rem);
        if (worst <= main.size()) {
          match = p1[pos] == suffix1[pos] && p2[pos] == suffix2[pos] && s1[rem] == prefix1[rem] && s2[rem] == prefix2[rem];
        }
        if (match) {
          for (int i = 1; i < s.size() && i <= k; ++i) {
            if (t[i - 1] == ch) {
              if (worst <= s[i - 1].size()) {
                res += pow2[k - i];
              }
            }
          }
          if (k >= s.size()) {
            res += pow2[k] * extraCount[k][ch - 'a'];
          }
        }
      }
      out << res.val() << "\n";
    }
  }

  void solve(std::istream &in, std::ostream &out) {
    int nt = 1;
    for (int it = 0; it < nt; ++it) {
      solveOne(in, out);
    }
  }
};


int main() {
  ios_base::sync_with_stdio(false);
  cin.tie(NULL);
  GSongOfTheSirens solver;
  std::istream& in(std::cin);
  std::ostream& out(std::cout);
  solver.solve(in, out);
  return 0;
}