class FUporotayaSvyaznost {
 public:
  void solveOne() {
    int n;
    cin >> n;
    atcoder::dsu comp(n);
    vector<int> d(n);
    for (auto& x : d) cin >> x;
    vector<int> seq(n);
    iota(all(seq), 0);
    sort(all(seq), [&](int a, int b) {
      return d[a] > d[b];
    });
    vector<bool> pending(n, true);
    for (int x : seq) if (pending[x]) {
      pending[x] = false;
      for (int i = 0; i < d[x]; ++i) {
        cout << "? " << (x + 1) << "\n";
        cout.flush();
        int y;
        cin >> y;
        --y;
        comp.merge(x, y);
        if (pending[y]) {
          pending[y] = false;
        } else {
          break;
        }
      }
    }
    cout << "!";
    for (int i = 0; i < n; ++i) cout << " " << (comp.leader(i) + 1);
    cout << "\n";
    cout.flush();
  }

  void solve() {
    int nt;
    cin >> nt;
    for (int it = 0; it < nt; ++it) {
      // cout << "Case #" << (it + 1) << ": ";
      solveOne();
      // cerr << "Case #" << (it + 1) << " done" << endl;
    }
  }
};

