using namespace std;

class TaskC {
public:
	void solve(std::istream& in, std::ostream& out) {
		//static int testnumber = 0;
		//out << "Case #" << ++testnumber << ": ";
		int n;
		in >> n;
		vector<int> l(n);
		vector<int> r(n);

		for (int i: range(n)) {
			in >> l[i];
		}
		for (int i: range(n)) {
			in >> r[i];
		}
		vector<int> ans(n);

		for (int i: range(n)) {
			ans[i] = n - l[i] - r[i];
		}

		for (int i: range(n)) {
			int cntl = 0;
			int cntr = 0;
			for (int j: range(i)) {
				if (ans[j] > ans[i]) {
					++cntl;
				}
			}
			for (int j: range(i + 1, n)) {
				if (ans[j] > ans[i]) {
					++cntr;
				}
			}

			if (cntl != l[i] || cntr != r[i]) {
				out << "NO\n";
				return;
			}
		}

		out << "YES\n";
		for (int x: ans) {
			out << x << " ";
		}
	}
};


int main() {
	std::ios_base::sync_with_stdio(false);
	TaskC solver;
	std::istream& in(std::cin);
	std::ostream& out(std::cout);
	in.tie(nullptr);
	out << std::fixed;
	out.precision(20);
	solver.solve(in, out);
	return 0;
}