using namespace std;

class CDrazilLyubitKuchi {
public:
	static constexpr int kStressCount = 1;
	static void generateTest(std::ostream& test) {
		int n = 20;
		test << "1\n" << n << ' ' << n - 1 << "\n";
		for(int i = (1 << n) - 1; i >= 1; --i) {
			test << i << " ";
		}
	}
	void solve(std::istream& in, std::ostream& out) {
		//static int testnumber = 0;
		//out << "Case #" << ++testnumber << ": ";

		int h, g;
		in >> h >> g;


		vector<int> a((1 << h) - 1);
		vector<int> valuesToLeave;
		for (int i: range((1 << h) - 1)) {
			in >> a[i];
		}

		int64_t ans = 0;

		auto dfs = [&](auto& dfs, int v) -> pair<int, vector<int>> {
			if (v >= a.size()) {
				return {0, {}};
			}
			auto [lv, ls] = dfs(dfs, 2 * v + 1);
			auto [rv, rs] = dfs(dfs, 2 * v + 2);
			ls.reserve(ls.size() * 2 + 1);
			ls.push_back(a[v]);
			ls.insert(ls.end(), rs.begin(), rs.end());
			sort(ls.begin(), ls.end());

			if (v < ((1 << g) - 1)) {
				auto cur = *upper_bound(ls.begin(), ls.end(), max(lv, rv));
				valuesToLeave.push_back(cur);
				ans += cur;
				//cerr << *ls.upper_bound(max(lv, rv)) << endl;
				return {cur, move(ls)};
			}
			return {0, move(ls)};
		};

		dfs(dfs, 0);
		out << ans << "\n";

		sort(valuesToLeave.begin(), valuesToLeave.end());

		for (int i: downrange(a.size())) {
			if (!binary_search(valuesToLeave.begin(), valuesToLeave.end(), a[i])) {
				out << i + 1 << " ";
			}
		}
		out << "\n";
	}
};


