using namespace std;

class BRasshifrovkaPovtoryashek {
public:
	static constexpr int kStressCount = 0;

	static void generateTest(std::ostream &test) {
	}

	void solve(std::istream &in, std::ostream &out) {
		//static int testnumber = 0;
		//out << "Case #" << ++testnumber << ": ";
		//cerr << "test " << testnumber << endl;


		int n;
		in >> n;
		vector<int> a(n);
		map<int, int> cnt;
		for (int i: range(n)) {
			in >> a[i];
			++cnt[a[i]];
		}

		for (auto&[v, c]: cnt) {
			if (c % 2) {
				out << -1 << "\n";
				return;
			}
		}

		vector<int> blocks;
		vector<pair<int, int>> inserts;

		while (!a.empty()) {
			int x = a.back();
			int pr = a.size() - 2;
			while (a[pr] != x) {
				--pr;
			}
			int index = pr;
			for (int i = a.size() - 2; i > pr; --i) {
				inserts.push_back({index, a[i]});
				++index;
			}
			blocks.push_back((a.size() - pr - 1) * 2);
			reverse(a.begin() + pr + 1, a.end() - 1);
			a.pop_back();
			a.erase(a.begin() + pr);
		}

		out << inserts.size() << "\n";
		for (auto p: inserts) {
			out << p.first << " " << p.second << "\n";
		}
		out << blocks.size() << "\n";
		for (int block: reversed(blocks)) {
			out << block << " ";
		}
		out << '\n';

	}
};


