using namespace std;

class BLinguistics {
public:
	static constexpr int kStressCount = 0;

	static void generateTest(std::ostream &test) {
	}

	void solve(std::istream &in, std::ostream &out) {
		//static int testnumber = 0;
		//out << "Case #" << ++testnumber << ": ";
		//cerr << "test " << testnumber << endl;

		int a, b, c, d;
		in >> a >> b >> c >> d;
		string s;
		in >> s;
		int ca = count(s.begin(), s.end(), 'A');
		if (a + b + c * 2 + d * 2 != s.size()) {
			out << "NO\n";
			return;
		}
		if (ca != a + c + d) {
			out << "NO\n";
			return;
		}
		int prev = 0;
		int any = 0;
		vector<int> abs, bas;
		for (int i = 1; i <= s.size(); ++i) {
			if (i == s.size() || s[i] == s[i - 1]) {
				if ((i - prev) % 2) {
					any += (i - prev) / 2;
				} else {
					//any += (i - prev - 1) / 2;
					if (s[i - 1] == 'B') {
						abs.push_back((i - prev) / 2);
					} else {
						bas.push_back((i - prev) / 2);
					}
				}
				prev = i;
			}
		}

		sort(abs.begin(), abs.end());
		sort(bas.begin(), bas.end());

		for (int x: abs) {
			if (c >= x) {
				c -= x;
			} else {
				any += x - 1;
			}
		}

		for (int x: bas) {
			if (d >= x) {
				d -= x;
			} else {
				any += x - 1;
			}
		}

//		cerr << c << ' ' << d << ' ' << any << endl;
		if (any >= c + d) {
			out << "YES\n";
		} else {
			out << "NO\n";
		}
	}
};


