class TaskE {
public:
	void solve(std::istream& in, std::ostream& out) {
		int n, maxCost;
		in >> n >> maxCost;

		MinCostFlow<int, int> flow(n);
		for (int i = 0; i < n; ++i) {
			for (int j = 0; j < n; ++j) {
				int c;
				in >> c;
				if(c == 0)
					continue;
				flow.addEdge(i, j, c, 0);
				flow.addEdge(i, j, maxCost, 1);
			}
		}

		out << flow.findFlow(0, n - 1, FixedCostMaxFlowStrategy<int>(maxCost)).flow;
	}
};


