using namespace std;

class TaskC {
public:
	void solve(std::istream& in, std::ostream& out) {
		int n, k;
		in >> n >> k;
		auto v = make_vector<int>(n, 4, 0);
		for (int i: range(n)) {
			for (int j: range(4)) {
				in >> v[i][j];
			}
			v[i][0] = v[i][2] = (v[i][2] + v[i][0]);
			v[i][1] = v[i][3] = (v[i][3] + v[i][1]);
		}
		auto indices = make_vector<int>(4, n, 0);
		for (int i = 0; i < 4; ++i)
			for (int j: range(n)) {
				indices[i][j] = j;
			}
		sort(indices[0], [&v](int a, int b){
			return v[a][0] < v[b][0];
		});
		sort(indices[1], [&v](int a, int b){
			return v[a][1] < v[b][1];
		});
		sort(indices[2], [&v](int a, int b){
			return v[a][2] > v[b][2];
		});
		sort(indices[3], [&v](int a, int b){
			return v[a][3] > v[b][3];
		});

		vector<int> a(4);
		long long ans = 1e18;
		for (a[0] = 0; a[0] <= k; ++a[0]) {
			for (a[1] = 0; a[1] <= k; ++a[1]) {
				for (a[2] = 0; a[2] <= k; ++a[2]) {
					for (a[3] = 0; a[3] <= k; ++a[3]) {
						set<int> se;
						for (int i = 0; i < 4; ++i) {
							se.insert(indices[i].begin(), indices[i].begin() + a[i]);
						}
						if (se.size() <= k) {
							long long v1 = f(v[indices[2][a[2]]][2], v[indices[0][a[0]]][0]);
							long long v2 = f(v[indices[3][a[3]]][3], v[indices[1][a[1]]][1]);
							//cerr << v1 << ' ' << v2 << endl;

							ans = min(ans, v1 * v2);
						}
					}
				}
			}
		}

		out << ans;
	}

	long long f(int b, int a) {
	//	cerr << a << ' ' << b << endl;
		if( a == b) {
			return 1;
		}
		return (b - a + 1) / 2;
	}
};


