template <typename T>
T binpow(T base, long long power) {
	SPCPPL_ASSERT(power >= 0);
	T result = identity<T>(base);
	SPCPPL_ASSERT(power >= 0);
	while (power > 0) {
		if (power & 1) {
			result *= base;
		}
		power >>= 1;
		base *= base;
	}
	return result;
}


using namespace std;

class TaskB {
public:
	void solve(std::istream& in, std::ostream& out) {
		int p, k;
		in >> p >> k;
		using Z = Zn<1000000007>;

		if(k == 0) {
			out << binpow(Z::valueOf(p), p - 1);
			return;
		}

		if (k == 1) {
			out << binpow(Z::valueOf(p), p);
			return;
		}
		int power = 1;
		long long cur = k;
		while (cur != Z::rawValueOf(1)) {
			cur *= k;
			cur %= p;
			++power;
		}

		out << binpow(Z::valueOf(p), (p - 1) / power);

	}
};


