class TwoIntsHasher {
public:

	static const uint64_t multiplier = 97;

	TwoIntsHasher(const std::string& s) {
		ensureLength(s.length());
		for (std::size_t j = 0; j < 2; ++j) {
			hashes[j].resize(s.length() + 1);
			for (std::size_t i: range(s.size())) {
				hashes[j][i + 1] = (hashes[j][i] * multiplier + s[i]) % modules[j];
			}
		}
	}

	uint64_t hash(std::size_t from, std::size_t to) const {
		SPCPPL_ASSERT(from <= to);
		return (oneHash(from, to, 0) << 32ULL) | (oneHash(from, to, 1));
	}

private:

	uint64_t oneHash(std::size_t from, std::size_t to, std::size_t index) const {
		uint64_t result = hashes[index][to] - hashes[index][from] * powers[index][to - from] % modules[index];
		if (result >= modules[index]) {
			result += modules[index];
		}
		return result;
	}

	static void ensureLength(std::size_t length) {
		for (std::size_t i = 0; i < 2; ++i) {
			std::vector<uint64_t>& powersArray = powers[i];
			powersArray.reserve(length + 1);
			while (powersArray.size() < length + 1) {
				powersArray.push_back(powersArray.back() * multiplier % modules[i]);
			}
		}
	}

	static std::array<std::vector<uint64_t>, 2> powers;
	std::array<std::vector<uint64_t>, 2> hashes;

	static uint64_t randomBigPrime() {
		return nextPrime(randomInteger(1000000000, 2000000000));
	}

	static const std::array<uint64_t, 2> modules;
};

std::array<std::vector<uint64_t>, 2> TwoIntsHasher::powers = {{{1}, {1}}};
const std::array<uint64_t, 2> TwoIntsHasher::modules = {{randomBigPrime(), randomBigPrime()}};





#include <algorithm>

template <typename Hasher>
class StringHash {
public:
	StringHash(std::string s): hasher(s), s(std::move(s)) {

	}

	uint64_t hash(std::size_t from, std::size_t to) const {
		return hasher.hash(from, to);
	}

	uint64_t prefixHash(std::size_t to) const {
		return hasher.hash(0, to);
	}

	uint64_t suffixHash(std::size_t from) const {
		return hasher.hash(from, hasher.size());
	}

	uint64_t hash() const {
		return hasher.hash(0, hasher.size());
	}

	bool less(std::size_t l1, std::size_t r1, std::size_t l2, std::size_t r2) {
		SPCPPL_ASSERT(l1 <= r1 && l2 <= r2);
		std::size_t min_len = std::min(r1 - l1, r2 - l2);
		if (s[l1] != s[l2]) {
			return s[l1] < s[l2];
		}
		if (hash(l1, l1 + min_len) == hash(l2, l2 + min_len)) {
			return r2 - l2 > r1 - l1;
		}
		std::size_t l = 0, r = min_len;
		while(r - l > 1) {
			std::size_t m = (r + l) / 2;
			if (hash(l1, l1 + m) == hash(l2, l2 + m)) {
				l = m;
			} else {
				r = m;
			}
		}

		auto res2 = s[l1 + l] < s[l2 + l];
		return res2;
	}

private:
	Hasher hasher;
	std::string s;
};




template <typename T, size_t N>
struct MakeVector {
	template <
			typename... Args,
			typename R = std::vector<decltype(MakeVector<T, N - 1>::make_vector(std::declval<Args>()...))>
	>
	static R make_vector(std::size_t first, Args... sizes) {
		auto inner = MakeVector<T, N - 1>::make_vector(sizes...);
		return R(first, inner);
	}
};

template <typename T>
struct MakeVector<T, 1> {
	/*
	 * This template is to fool CLion.
	 * Without it CLion thinks that make_vector always returns std::vector<T> and marks code like
	 *
	 * auto dp = make_vector<int>(n, m, 0);
	 * dp[0][0] = 1 as error because it suppose that dp[0] is int
	 *
	 * TODO: Consider removing it once https://youtrack.jetbrains.com/issue/CPP-3340 is fixed
	 */
	template <typename R = std::vector<T>>
	static R make_vector(std::size_t size, const T& value) {
		return R(size, value);
	}
};

template <typename T, typename... Args>
auto make_vector(Args... args) -> decltype(MakeVector<T, sizeof...(Args) - 1>::make_vector(args...)) {
	return MakeVector<T, sizeof...(Args) - 1>::make_vector(args...);
}







class OverflowHasher {
public:
	static const unsigned long long multiplier = 97;

	OverflowHasher(const std::string& s): hashes(s.size() + 1) {
		ensureLength(s.length());
		for (std::size_t i: range(s.size())) {
			hashes[i + 1] = hashes[i] * multiplier + s[i];
		}
	}

	unsigned long long hash(std::size_t from, std::size_t to) const {
		SPCPPL_ASSERT(from <= to);
		return hashes[to] - hashes[from] * powers[to - from];
	}

private:

	static void ensureLength(std::size_t length) {
		powers.reserve(length + 1);
		while (powers.size() < length + 1) {
			powers.push_back(powers.back() * multiplier);
		}
	}

	static std::vector<unsigned long long> powers;
	std::vector<unsigned long long> hashes;
};

std::vector<unsigned long long> OverflowHasher::powers = {1};


using namespace std;

using Z = Zn<1000000007>;
array<array<Z, 5001>, 5001> dp;
class TaskD {
public:

	void solve(std::istream& in, std::ostream& out) {
		int n;
		string s;
		in >> n >> s;

		dp[1][0] = Z::rawValueOf(1);

		srand(time(0));
		randomEngine().seed(rand());
		StringHash<TwoIntsHasher> hasher(s);

		for (int r: range(1, n)) {
			for (int prev: range(r)) {
				if(dp[r][prev] == Z())
					continue;
				if (n - r < r - prev) {
					dp[n][r] += dp[r][prev];
					continue;
				}
				dp[r + 1][prev] += dp[r][prev];
				if (s[r] == '0')
					continue;
				int cand = 2 * r - prev;
				if (cand > n) {
					continue;
				}
				if (hasher.less(prev, r, r, cand)) {
					dp[cand][r] += dp[r][prev];
				}
				else {
					++cand;
					if(cand > n) {
						continue;
					}
					dp[cand][r] += dp[r][prev];
				}
			}
		}
		Z ans;
		for (int i: range(n)) {
			ans += dp[n][i];
		}

		out << ans << "\n";
	}

};



int main() {
	std::ios_base::sync_with_stdio(false);
	TaskD solver;
	std::istream& in(std::cin);
	std::ostream& out(std::cout);
	in.tie(0);
	out << std::fixed;
	out.precision(20);
	solver.solve(in, out);
	return 0;
}