using namespace std;

class TaskC {
public:
	void solve(std::istream& in, std::ostream& out) {
		using Point = Point2D<int64_t, int64_t>;
		Point p;
		int n;
		in >> n;
		vector<Point> points(n);
		vector<int> indices(n);
		for (int i: range(n)) {
			in >> points[i].x >> points[i].y;
			indices[i] = i;
		}

		sort(indices, [&points](int a, int b) {
			return std::tie(points[a].x, points[a].y) < std::tie(points[b].x, points[b].y);
		});

		int left_index = indices[0];
		Point left = points[indices[0]];
		indices.erase(indices.begin());
		for (int i: range(n)) {
			points[i] -= left;
		}

		sort(indices, [&points](int a, int b) {
			Point A = points[a];
			Point B = points[b];
			if (A * B != 0) {
				return A * B > 0;
			}
			return A.squaredDist() < B.squaredDist();
		});


		int second_index = indices[0];

		int t = 0;
		while (points[second_index] * points[indices[t]] == 0) {
			++t;
		}

		int third_index = indices[t];

		out << left_index + 1 << ' ' << second_index + 1 << ' ' << third_index + 1 << "\n";


	}
};


