using namespace std;

class TaskD {
public:
	void solve(std::istream& in, std::ostream& out) {
		int n;
		int64_t x, y;
		in >> n >> x >> y;
		vector<set<int>> g(n);

		for(int i: range(n - 1)) {
			int a, b;
			in >> a >> b;
			--a, --b;
			g[a].insert(b);
			g[b].insert(a);
		}
		if (x >= y) {
			bool ex = false;
			for (int i: range(n)) {
				if (g[i].size() == n - 1) {
					ex = true;
				}
			}
			if (ex) {
				out << x + (n - 2) * y;
			} else {
				out << (n - 1) * y;
			}
			return;
		}

		vector<int> used_times(n);
		vector<int> processed(n);

		int times_small = 0;
		deque<int> to_process;
		for (int i: range(n)) {
			if (g[i].size() == 1) {
				to_process.push_back(i);
			}
		}
		while(!to_process.empty()) {
			int v = to_process.front();
			to_process.pop_front();
			if(g[v].empty()) {
				continue;
			}
			processed[v] = true;
			bool good_now = used_times[v] < 2;
			//cerr << v << ' ' << good_now << endl;
			times_small += good_now;
			for (int to: g[v]) {
				if (processed[to]) {
					continue;
				}
				used_times[to] += good_now;
				g[to].erase(v);
				if (used_times[to] >= 2 || g[to].size() == 1) {
					if (used_times[to] < 2) {
						to_process.push_back(to);
					}
					else {
						to_process.push_front(to);
					}
				}
			}

		}

		out << times_small * x + (n - 1 - times_small) * y;
	}
};


int main() {
	std::ios_base::sync_with_stdio(false);
	TaskD solver;
	std::istream& in(std::cin);
	std::ostream& out(std::cout);
	in.tie(0);
	out << std::fixed;
	out.precision(20);
	solver.solve(in, out);
	return 0;
}